mapper.select:
select *
from post p
         left join postPic pp on p.p_no = pp.pp_no
where p.p_id = #{p_id}
order by p_indate desc;

select pp_pic
from postPic
where pp_no = #{pp_no}

select *
from postComment
where pc_no = #{p_no}

mapper.update:
update post
set p_place = #{p_place},
    p_text  = #{p_text}
where p_no = #{p_no}

mapper.select:
select count(*)
from postComment
where pc_no = #{p_no}

mapper.delete:
delete
from postLike
where pl_id = #{p_id}
  and pl_no = #{p_no}

mapper.select:
select *
from postLike
where pl_id = #{p_id}
  and pl_no = #{p_no}

select count(*)
from postLike
where pl_no = #{p_no}

mapper.update:
update short_form
set s_description  = #{s_description},
    s_place = #{s_place}
where s_no = #{s_no}

mapper.select:
select *
from short_form
order by s_date desc;

select *
from short_form
where s_id = #{u_id}

select *
from short_form
where s_no = #{s_no}

select *
from short_video
where sv_no = #{sv_no}

select *
from short_comment
where s_no = #{s_no}

mapper.delete:
delete
from short_comment
where s_no = #{s_no}
  AND sc_date = #{sc_date}
  AND sc_id = #{sc_id}

mapper.update:
update post
set p_views = p_views + 1
where p_no = #{p_no}

mapper.select:
select p_views
from post
where p_no = #{p_no}
